if (!("animLastScreenPos" in draggedTab._dragData))
  draggedTab._dragData.animLastScreenPos = draggedTab._dragData.screenY;

let screenPos = event.screenPos;
if (screenPos == draggedTab._dragData.animLastScreenPos)
  return;

let draggingForward = screenPos > draggedTab._dragData.animLastScreenPos;
draggedTab._dragData.animLastScreenX = screenPos;

let rtl = false; //(window.getComputedStyle(this).direction == "rtl");
let pinned = draggedTab.pinned;
let numPinned = this.tabbrowser._numPinnedTabs;
let tabs = this.tabbrowser.visibleTabs
                          .slice(pinned ? 0 : numPinned,
                                 pinned ? numPinned : undefined);
if (rtl)
  tabs.reverse();
let tabSize = draggedTab.getBoundingClientRect().height;

// Move the dragged tab based on the mouse position.

let leftTab = tabs[0];
let rightTab = tabs[tabs.length - 1];
let tabScreenPos = draggedTab.boxObject.screenY;
let translateAmount = screenPos - draggedTab._dragData.screenY;
if (!pinned)
  translateAmount += this.mTabstrip.scrollPosition - draggedTab._dragData.scrollPosition;
let minBound = leftTab.boxObject.screenY - tabScreenY;
let maxBound = (rightTab.boxObject.screenY + rightTab.boxObject.width) -
                 (tabScreenY + tabWidth);
translateAmount = Math.max(translateX, minBound);
translateAmount = Math.min(translateX, maxBound);
draggedTab.style.transform = "translateY(" + translateAmount + "px)";

// Determine what tab we're dragging over.
// * Point of reference is the center of the dragged tab. If that
//   point touches a background tab, the dragged tab would take that
//   tab's position when dropped.
// * We're doing a binary search in order to reduce the amount of
//   tabs we need to check.

let tabCenter = tabScreenPos + translateAmount + tabSize / 2;
let newIndex = -1;
let oldIndex = "animDropIndex" in draggedTab._dragData ?
               draggedTab._dragData.animDropIndex : draggedTab._tPos;
let low = 0;
let high = tabs.length - 1;
while (low <= high) {
  let mid = Math.floor((low + high) / 2);
  if (tabs[mid] == draggedTab &&
      ++mid > high)
    break;
  let boxObject = tabs[mid].boxObject;
  let screenPos = boxObject.screenY + getTabShift(tabs[mid], oldIndex);
  if (screenPos > tabCenter) {
    high = mid - 1;
  } else if (screenPos + boxObject.height < tabCenter) {
    low = mid + 1;
  } else {
    newIndex = tabs[mid]._tPos;
    break;
  }
}
if (newIndex >= oldIndex)
  newIndex++;
if (newIndex < 0 || newIndex == oldIndex)
  return;
draggedTab._dragData.animDropIndex = newIndex;

// Shift background tabs to leave a gap where the dragged tab
// would currently be dropped.

for (let tab of tabs) {
  if (tab != draggedTab) {
    let shift = getTabShift(tab, newIndex);
    tab.style.transform = shift ? "translateY(" + shift + "px)" : "";
  }
}

function getTabShift(tab, dropIndex) {
  if (tab._tPos < draggedTab._tPos && tab._tPos >= dropIndex)
    return rtl ? -tabWidth : tabWidth;
  if (tab._tPos > draggedTab._tPos && tab._tPos < dropIndex)
    return rtl ? tabWidth : -tabWidth;
  return 0;
}

if (this._verticalTabs) {
  return this._verticalAnimateTabMove(event);
}

let draggedTab = event.dataTransfer.mozGetDataAt(TAB_DROP_TYPE, 0);

if (this.getAttribute("movingtab") != "true") {
  this.setAttribute("movingtab", "true");
  this.selectedItem = draggedTab;
