  let boxObject = tabs[mid].boxObject;
  let screenX = boxObject.screenX + getTabShift(tabs[mid], oldIndex);
  if (screenX > tabCenter) {
    high = mid - 1;
  } else if (screenX + boxObject.width < tabCenter) {
    low = mid + 1;
  } else {
    newIndex = tabs[mid]._tPos;
    break;
  }
}
if (newIndex >= oldIndex)
